import React, { Dispatch, useState, useEffect } from "react";
import { connect } from "react-redux";
import { Button } from "primereact/button";
import { Dropdown } from "primereact/dropdown";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { createBrowserHistory } from "history";
import formNameDataJSON from '../../../assets/data/Forms_JSON/formNameData.json';
import FormCreateData from '../../../assets/data/FormCreateData.json';
import { InputText } from "primereact/inputtext";
import {
  FormControl,
  InputLabel,
  FormHelperText,
  Select,
  TextField,
  MenuItem
} from "@mui/material";
import "././../Bed/BedMasterConfiguration.css";
import { HttpLogin } from "../../../utils/Http";
import { createForms,getAllForms } from "../../../store/actions/TreatmentPlan";

interface IViewFields {

  dispatch: Dispatch<any>;
  getAllFormsData:any;
}
const ViewFields: React.FC<IViewFields> = ({
  dispatch,getAllFormsData
}) => {
  const history = createBrowserHistory();
  let [allFormName, setAllFormName] = useState([]);
  let [inputOrgData, setInputOrgData] = useState("");


  useEffect(() => {
    var orgData = JSON.parse(window.localStorage.getItem("LOGINDATA"));
    orgData = orgData.loginInput.organization;
    HttpLogin.axios().get("api/org/getById/" + orgData)
        .then((res) => {
            if (res.data.message.code === "MHC - 0200") {
              setInputOrgData(res.data.data.organizationdetails[0].name);
              inputFormData.organization = res.data.data.organizationdetails[0].name;
              setInputFormData({ ...inputFormData });
            } else {
                alert(res.data.message.description); 
                setInputOrgData("");               
            }
        })
        var phrase="this is a test";
        for (var i=0; i<phrase.length; i++) {
            if(phrase.substring(i,i+1).includes(" ")) {
              console.log(JSON.stringify(phrase.substring(i+1,i+2)))             
                phrase = phrase.replace(phrase.substring(i,i+2),phrase.substring(i,i+2).toUpperCase());
            }
        }
console.log(JSON.stringify(phrase.replaceAll(" ", "_")))
        dispatch(getAllForms());       
  }, [])
  
  let [inputFormData, setInputFormData] = useState(FormCreateData);

  const handleInputChange = (event: any) => {
    if (event.target.id === "selectFormName") {
      inputFormData.name = event.target.value;
    } if (event.target.name === "selectFormName") {
      inputFormData.name = event.target.value;
    }
    setInputFormData({ ...inputFormData });
  }


  const handleAddForm = (event: any) => {
    
    allFormName.map((x) => {
      if (x.name === inputFormData.name) {
        var CryptoJS = require("crypto-js");
        var encryptId = CryptoJS.AES.encrypt(x.name, 'secret key 123');
        var setEncryptId = encodeURIComponent(encryptId.toString());
        return true ? window.location.href = "/mettlerAddFields/"+setEncryptId : alert("Form not created");
      }
    })   
  }

  const [isPageLoaded, setPageLoaded] = useState(false);

  if (!isPageLoaded && !getAllFormsData.isLoading) {

      if (getAllFormsData.items.length > 0) {
    
        setAllFormName(getAllFormsData.items.filter(t => t.organization === inputOrgData));     
 
      } else {
          alert("No data found");
      }
      setPageLoaded(true);
     
  }
  if (!getAllFormsData && getAllFormsData.isFormSubmit) {

      setTimeout(() => {
          setPageLoaded(false);

      }, (1000));
  }

  
  const handleCheckForm = (event: any) => {
    // allFormName.map((x) => {
    //   if (x.shortName === inputFormData.selectFormName) {
    //     return x.urlName ? window.location.href = x.urlName : alert("Form not created");
    //   }
    // })
    allFormName.map((x) => {
      if (x.name === inputFormData.name) {
        return true ? window.location.href = "/MettlerAIMS" : alert("Form not created");
      }
    })
  }
 
  let newFormName = allFormName != null && allFormName.length > 0 && allFormName.map((item, i) => {
    return (
      <MenuItem key={i} value={item.name}>{item.name}</MenuItem>
    )
  })

  const handleCreateAddForm = (event: any) => {
    setInputFormData({ ...inputFormData });
    dispatch(createForms(inputFormData));
    // var name = inputFormData.addFormName;
    // var matches = name.match(/\b(\w)/g);
    // var acronym = matches.join('').toUpperCase();
    // console.log(JSON.stringify(acronym));
    // var arrayData;

    // allFormName.map(x => {
    //   let newCaseName = {
    //     "name": inputFormData.addFormName,
    //     "shortName": acronym,
    //     "urlName": "/Mettler" + acronym
    //   }
    //   arrayData = allFormName;
    //    console.log(JSON.stringify(arrayData))
    //   arrayData = arrayData.push(newCaseName)
    // })
    // console.log(JSON.stringify(arrayData))
  }


  const handleCreateNewForm = (event: any) => {
    window.location.href = "/MettlerCreateNewForm";
  }

  return (
    <div id="removePaddingTop" style={{ backgroundColor: "white" }} className="p-grid p-fluid ">
      <div id="removePadding" className="p-col-12 p-md-12">

        <h3 style={{ fontWeight: 600, position: 'relative', marginTop:'30px', top:'-10px', left: '599px', width: "100%", color: "#292F52", fontFamily: "poppins" }}> Add/edit Field Details</h3>

      </div>
      <div></div>
      <div style={{ border: "1px solid #DEE5ED", position: 'relative', height: '1px', top: '-17px', width: "calc(100% - 10px)" }} />
      <div id="removePadding" className="p-col-2 p-md-2" style={{left:"496px",position:"relative"}}>
        <Button style={{ backgroundColor: "#133C93", borderColor: "#133C93" }} type="button" label="Create New Form Fields" onClick={handleCreateNewForm}></Button>
      </div>

      <div id="removePadding" className="p-col-2 p-md-2"style={{left:"530px",position:"relative"}}>
        <Button style={{ backgroundColor: "#133C93", borderColor: "#133C93" }} type="button" label="View/Update Form Fields" onClick={null}></Button>
      </div>
      <div id="removePadding" className="p-col-8 p-md-8">
      </div>
      <div id="removePadding" className="p-col-12 p-md-12"></div>
      <div id="removePadding" className="p-col-12 p-md-1">
        Form Name:
      </div>
      <div id="removePadding" className="p-col-12 p-md-4">
      <TextField
              style={{ width: "354px" }}
              id="selectFormName" value={inputFormData.name} onChange={handleInputChange}
              className="name-input13"
              color="primary"
              variant="outlined"
              type="text"            
              size="medium"
              margin="none"
              required
            />
        {/* <InputText id="addFormName" onChange={handleInputChange} value={inputFormData.addFormName} required /> */}
      </div>
      <div id="removePadding" className="p-col-12 p-md-7">

      </div>
      <div id="removePadding" className="p-col-12 p-md-1">
        Short Name:
      </div>
      <div id="removePadding" className="p-col-12 p-md-4">
      <TextField
              style={{ width: "354px" }}
              id="addShortName" value={null} onChange={handleInputChange}
              className="name-input13"
              color="primary"
              variant="outlined"
              type="text"
            
              size="medium"
              margin="none"
              required
            />
        {/* <InputText id="addShortName" onChange={handleInputChange} value={inputFormData.addShortName} required /> */}
      </div>
      <div id="removePadding" className="p-col-12 p-md-7">
      </div>
      <div id="removePadding" className="p-col-12 p-md-1">
        Url Name:
      </div>
      <div id="removePadding" className="p-col-12 p-md-4">
      <TextField
              style={{ width: "354px" }}
              id="addUrlName" value={null} onChange={handleInputChange}
              className="name-input13"
              color="primary"
              variant="outlined"
              type="text"            
              size="medium"
              margin="none"
              required
            />
        {/* <InputText id="addUrlName" onChange={handleInputChange} value={inputFormData.addUrlName} required /> */}
      </div>
      <div id="removePadding" className="p-col-12 p-md-7">
      </div>
      <div id="removePadding" className="p-col-12 p-md-2">
      </div>
      <div id="removePadding" className="p-col-12 p-md-4">
        <Button label="Create New Form" style={{ width: 'fit-content', backgroundColor: "#133C93", borderColor: "#133C93" }} onClick={handleCreateAddForm}></Button>
      </div>
      <div id="removePadding" className="p-col-12 p-md-6">
      </div>

      <div className="p-col-3">
        <div className="p-grid">

          <div id="removePadding" className="p-col-12 p-md-12" style={{ fontFamily: "poppins", fontWeight: 400, }}>
            Select the Form Name
          </div>
          <div id="removePadding" className="p-col-12 p-md-12">
            <FormControl className="name-input13" variant="outlined" style={{ width: '354px' }}>
              <InputLabel color="primary" >Select<span>
              </span></InputLabel>
              <Select id="selectFormName" size="medium" label="Select" name="selectFormName" value={inputFormData.name} required onChange={handleInputChange}>
                {newFormName}
              </Select>
              <FormHelperText />
            </FormControl>
            {/* <select  name="selectFormName" style={{ width: '95%' }} onChange={handleInputChange} value={inputFormData.selectFormName} required >
              <option value="">Select</option>
              
            </select> */}
          </div>
          <div id="removePadding" className="p-col-12 p-md-12">
            <Button style={{ backgroundColor: "#133C93", borderColor: "#133C93" }} type="button" label="Customize Form" onClick={null}></Button>
          </div>

          <div id="removePadding" className="p-col-12 p-md-12">
            <Button style={{ backgroundColor: "#133C93", borderColor: "#133C93",top:'9px',position:"relative" }} type="button" label="Preview Form" onClick={handleCheckForm}></Button>
          </div>
          <div id="removePadding" className="p-col-12 p-md-12"> </div>
          <div id="removePadding" className="p-col-12 p-md-12">
            <Button style={{ backgroundColor: "#133C93", borderColor: "#133C93" }} type="button" label="Delete Form" onClick={null}></Button>
          </div>

        </div>
      </div>
      <div className="p-col-1"></div>

      <div className="p-col-8" >
        <div className="p-grid">
          <div id="removePadding" className="p-col-12 p-md-12"> </div>

          <div id="removePadding" className="p-col-5 p-md-5">
            <Button style={{ position: "relative", top: "22px", backgroundColor: "#133C93", borderColor: "#133C93" }} type="button" label="Add New Fields/Question " onClick={handleAddForm}></Button>
          </div>
          <div id="removePadding" className="p-col-7 p-md-7"> </div>


        </div>
      </div>
      <DataTable style={{ border: '0px', top: "6px", left: "10px" }}
        value={null}
        selectionMode="multiple"
        rows={50} scrollable={true}
        responsive={true} 
        emptyMessage="No records found">
        <Column selectionMode="multiple" headerStyle={{ width: '4%', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '5%', background: '#FFF' }}></Column>
        <Column field="" header="s.no" headerStyle={{ position: "relative", left: "9px", whiteSpace: "nowrap", width: '10%', textAlign: 'start', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '20%', background: '#FFF' }} />
        <Column field="" header="Select " headerStyle={{ position: "relative", left: "-59px", whiteSpace: "nowrap", width: '14%', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '23%', background: '#FFF' }} body={""} />
        <Column field="" header="Delete" headerStyle={{ position: "relative", left: "-18px", whiteSpace: "nowrap", width: '8%', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '15%', background: '#FFF' }} body={""} />
        <Column field="" header="Display text" headerStyle={{ position: "relative", left: "-4px", whiteSpace: "nowrap", width: '16%', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '29%', background: '#FFF' }} body={""} />
        <Column field="" header="Default value" headerStyle={{ position: "relative", left: "-4px", whiteSpace: "nowrap", width: '17%', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '27%', background: '#FFF' }} body={""} />
        <Column field="" header="Position" headerStyle={{ position: "relative", left: "-2px", whiteSpace: "nowrap", width: '17%', background: '#F2F4F9', color: '#9DA1C3', fontFamily: 'Poppins', fontSize: '12px', fontWeight: 500 }} style={{ borderLeft: '0px', borderRight: '0px', width: '27%', background: '#FFF' }} body={""} />

      </DataTable>
      {/* <DataTable paginatorPosition="bottom" selectionMode="single"
        value={null}
        paginator={true} rows={10}
        alwaysShowPaginator={false}
        scrollable={true} scrollHeight="200px"
        header={""}
        emptyMessage="No Record Found">
        <Column field="" style={{ width: "5%" }} header="s.no" />
        <Column field="" body={""} style={{ width: "10%" }} header="Select" />
        <Column field="" body={""} style={{ width: "10%" }} header="Delete" />
        <Column field="" body={""} header="Display text" />
        <Column field="" body={""} style={{ width: "12%" }} header="Default value" />
        <Column field="" body={""} style={{ width: "12%" }} header="Position" />
      </DataTable> */}

      <div className="p-col-12 p-md-12">  </div>
      <div id="removePadding" className="p-col-8 p-md-8">  </div>
      <div className="p-col-4 p-md-4">
        <Button  icon="pi pi-step-backward" style={{ float: "right", width: 'fit-content', backgroundColor: "#133C93", borderColor: "#133C93" }} label="Back" onClick={() => history.goBack()} />

      </div>
    </div>

  );
};

const mapStateToProps = (state: any) => {
  const { deviceFormData, getAllFormsData } = state;
  return {
    deviceFormData, getAllFormsData
  };
};
export default connect(mapStateToProps)(ViewFields)
