import React, { useContext } from 'react'
import { FormContext } from '../../../../FormContext';
const Select = ({ filedId, fieldLabel, fieldPlaceholder, filedValue, fieldOptions, fieldSize }) => {
    const { handleChange } = useContext(FormContext)
    if(fieldSize === "Small"){
    return (
        <>
           <div id="removePadding" className="p-col-12 p-md-2" style={{fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 400,color: "#8494B7" ,}}><span>{fieldLabel}</span></div>
           <div id="removePadding" className="p-col-12 p-md-4 ">  
           <select className='orgName' style={{width:'-webkit-fill-available',fontSize:'20px'}}
                onChange={(event,i) => handleChange(filedId, event,i)}
            >
                <option style={{fontFamily:'Times New Roman'}}>Select</option>
                {fieldOptions.length > 0 && fieldOptions.map((option, i) =>
                    <option value={option.optionLabel} key={i}>{option.optionLabel}</option>

                )}
            </select>
            </div>
        </>
    )
    
    }else if(fieldSize === "Medium"){
        return (
            <>
               <div id="removePadding" className="p-col-12 p-md-6" style={{fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 400,color: "#8494B7" ,textAlign:"justify",whiteSpace:"nowrap"}}><span>{fieldLabel}</span></div>
               <div id="removePadding" className="p-col-12 p-md-4">  
               <select className='orgName' style={{width:'-webkit-fill-available',fontFamily:"Times New Roman",fontSize:'15px',fontWeight:"lighter",textAlign:"justify",height:'100%'}}
                    onChange={event => handleChange(filedId, event)}
                >
                    <option style={{fontFamily:"Times New Roman",fontSize:'15px',fontWeight:"lighter",textAlign:"justify"}}>Open this select menu</option>
                    {fieldOptions.length > 0 && fieldOptions.map((option, i) =>
                        <option value={option.optionLabel} key={i}>{option.optionLabel}</option>
    
                    )}
                </select>
                </div>
                <div id="removePadding" className="p-col-12 p-md-2">  </div>
            </>
        )
    }else{
        return (
            <>
               <div id="removePadding" className="p-col-12 p-md-12" style={{fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 400,color: "#8494B7" ,textAlign:"justify",position:'relative',left:'80px',width:'calc(100% - 157px)',whiteSpace:"nowrap"}}><span>{fieldLabel}</span></div>
               <div id="removePadding" className="p-col-12 p-md-12">  
               <select className='orgName' style={{width:'-webkit-fill-available',fontFamily:"Times New Roman",fontSize:'15px',fontWeight:"lighter",textAlign:"justify",height:'100%',position:'relative',left:'80px',width:'calc(100% - 157px)'}}
                    onChange={event => handleChange(filedId, event)}
                >
                    <option style={{fontFamily:"Times New Roman",fontSize:'15px',fontWeight:"lighter",textAlign:"justify"}}>Select</option>
                    {fieldOptions.length > 0 && fieldOptions.map((option, i) =>
                        <option value={option.optionLabel} key={i}>{option.optionLabel}</option>
    
                    )}
                </select>
                </div>              
            </>
        )
    }
}

export default Select
