import React, { useContext } from 'react'
import { FormContext } from '../../../../FormContext';
const Checkbox = ({ filedId, fieldLabel, filedValue,fieldSize }) => {
    const { handleChange } = useContext(FormContext)
    if(fieldSize === "Medium"){
    return (
        <>
               <div id="removePadding" className="p-col-12 p-md-1">
            <input type="checkbox" checked={filedValue} style={{height:'21px',width:'24px'}}
                onChange={(event,i) => handleChange(filedId, event,i)}
            />
          
            </div>            
            <div id="removePadding" className="p-col-12 p-md-5" style={{fontFamily:"Times New Roman",fontSize:'17px',fontWeight:"lighter",textAlign:"justify"}}>{fieldLabel}</div>
        </>
    )
    }else{
        return (
            <>
                   <div id="removePadding" className="p-col-12 p-md-1">
                <input type="checkbox" checked={filedValue} style={{height:'21px',width:'24px'}}
                    onChange={event => handleChange(filedId, event)}
                />
              
                </div>            
                <div id="removePadding" className="p-col-12 p-md-11" style={{fontFamily:"Times New Roman",fontSize:'17px',fontWeight:"lighter",textAlign:"justify"}}>{fieldLabel}</div>
            </>
        )
    }
}

export default Checkbox
