import React, { useContext, useState } from 'react'
import { FormContext } from '../../../../FormContext';
import './input.css';
const Calendar = ({ filedId, fieldLabel, fieldPlaceholder, filedValue, fieldSize }) => {
    const { handleChange } = useContext(FormContext)
    const [isVisible, setIsVisible] = useState(false);
    const toggleVisibility = () => {
        setIsVisible(true);
    }
    if (fieldSize === "Medium") {
        return (
            <>
                <div id="PaddingRemove" className="p-col-12 p-md-2" style={{ fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 400 ,color: "#8494B7" ,top:"-25px",position:'relative',left:"67px",height:"27px"}}><span>{fieldLabel}</span></div>
                <div id="PaddingRemove" className="p-col-12 p-md-4" style={{position:"relative",left:"-107px",top:'2px'}}>
                    <input type="date" className='floating__input' style={{ width: '110%', fontSize: '20px', height: isVisible ? "94%" : "94%" }}
                        placeholder={fieldPlaceholder ? fieldPlaceholder : ''}
                        onClick={toggleVisibility}
                        value={filedValue}
                        onChange={(event, i) => handleChange(filedId, event, i)}
                    />
                    {/* {isVisible && (
                        <div style={{ backgroundColor: "#ffffff", position: "relative", top: "-75px", left: "14px", width: "fit-content" }}>DD/MM/YYYY</div>
                    )} */}
                     {/* <label for="input__username" className="floating__label" data-content={fieldPlaceholder}>
                        <span className="hidden--visually"></span></label> */}
                </div>

            </>
        )
    } else {
        return (
            <>
                <div id="PaddingRemove" className="p-col-12 p-md-6" style={{ fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 700,top:"-25px",position:'relative',left:"67px",height:"27px" }}><span>{fieldLabel}</span></div>
                <div id="PaddingRemove" className="p-col-12 p-md-6" style={{position:"relative",left:"-107px",top:'2px'}}>
                    <input type="date" className='floating__input' style={{ width: '110%', fontSize: '20px', height: isVisible ? "94%" : "94%" }}
                        placeholder={fieldPlaceholder ? fieldPlaceholder : ''}
                        onClick={toggleVisibility}
                        value={filedValue}
                        onChange={event => handleChange(filedId, event)}
                    />
                    {/* {isVisible && (
                        <div style={{ backgroundColor: "#ffffff", position: "relative", top: "-75px", left: "14px", width: "fit-content" }}>DD/MM/YYYY</div>
                    )} */}
                     {/* <label for="input__username" className="floating__label" data-content={fieldPlaceholder}>
                        <span className="hidden--visually"></span></label> */}
                </div>

            </>
        )
    }
}

export default Calendar
