import React, { useContext, useState } from 'react'
import { FormContext } from '../../../../FormContext';
import './input.css';
// import { Select } from 'antd';

const Input = ({ filedId, fieldLabel, fieldPlaceholder, filedValue, fieldSize }) => {
    const { handleChange } = useContext(FormContext)
    const [isVisible, setIsVisible] = useState(false);
    const toggleVisibility = () => {
        setIsVisible(true);
    }
    if (fieldSize === "Medium") {
        return (
            //<>
            //      <div id="removePadding" className="p-col-12 p-md-2" style={{fontFamily:"Times New Roman",fontSize:'20px',fontWeight:700}}><span>{fieldLabel}</span></div>
            //      <div id="removePadding" className="p-col-12 p-md-4">
            //     <input type="text" className='orgName' style={{width:'-webkit-fill-available',fontSize:'20px'}}
            //         placeholder={fieldPlaceholder ? fieldPlaceholder : ''}
            //         value={filedValue}
            //         onChange={(event,i) => handleChange(filedId, event,i)}
            //     />
            //       </div>
            // </>
            <>
                <div id="PaddingRemove" className="p-col-12 p-md-2" style={{ fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 400,color: "#8494B7" ,top:"-25px",position:'relative',left:"72px",height:"27px" ,whiteSpace:"nowrap"}}><span>{fieldLabel}</span></div>
                <div id="PaddingRemove" className="p-col-12 p-md-4" style={{position:"relative",left:"-104px",top:"0px"}}>
                    <input value={filedValue}
                        className="floating__input"
                        onChange={(event, i) => handleChange(filedId, event, i)}
                        style={{ width: '110%', fontSize: '20px', height: isVisible ? "81%" : "" }}
                        placeholder={fieldPlaceholder ? fieldPlaceholder : ''} />
                    
                </div >
            </>

        )
    } else {
        return (
            // <>
            //      <div id="removePadding" className="p-col-12 p-md-6" style={{fontFamily:"Times New Roman",fontSize:'20px',fontWeight:700}}><span>{fieldLabel}</span></div>
            //      <div id="removePadding" className="p-col-12 p-md-6">
            //     <input type="text" className='orgName' style={{width:'-webkit-fill-available',fontSize:'20px'}}
            //         placeholder={fieldPlaceholder ? fieldPlaceholder : ''}
            //         value={filedValue}
            //         onChange={event => handleChange(filedId, event)}
            //     />
            //       </div>
            // </>
            <>
                <div id="PaddingRemove" className="p-col-12 p-md-2" style={{ fontFamily: "Times New Roman", fontSize: '15px', fontWeight: 400 ,top:"-25px",position:'relative',left:"32px",height:"27px",whiteSpace:"nowrap" }}><span>{fieldLabel}</span></div>
                <div id="PaddingRemove" className="p-col-12 p-md-4" style={{position:"relative",left:"-104px",top:"0px"}}>
                    <input value={filedValue}
                        style={{ width: '110%', fontSize: '20px', height: isVisible ? "81%" : "" }}
                        className="floating__input"
                        onChange={(event, i) => handleChange(filedId, event, i)}
                        placeholder={fieldPlaceholder ? fieldPlaceholder : ''}
                    />
           
                </div>
            </>
        )
    }
}

export default Input;
