import React, { useContext } from 'react'
import { FormContext } from '../../../../FormContext';
const Option = ({ fieldOptions,filedId }) => {
    const { handleChange } = useContext(FormContext)

    return (
 <>
       {fieldOptions.length>0 && fieldOptions.map((item,i)=>
        <>
         <div id="removePadding" className="p-col-12 p-md-1">
            <input type="radio" checked={item.optionValue} id={filedId} style={{height:'21px',width:'24px'}}
                onChange={(event) => handleChange(item.optionLabel, event,filedId)}
            />
          
            </div>            
            <div id="removePadding" className="p-col-12 p-md-11" style={{fontFamily:"Times New Roman",fontSize:'20px',fontWeight:700}}><span style={{marginLeft:'-81px'}}>{item.optionLabel}</span></div>
        </>
        )}
                      
              
        </>
    )
   
}

export default Option
